	public class PPCostCenterChildsController:BaseBusinessController
	{
		public PPCostCenterChildsController()
		{
			dal= new DALBaseProvider("PPCostCenterChilds",typeof(PPCostCenterChildsInfo));
		}

        public DataSet GetAllDataChildByCostCenterParent(int iPPCostCenterParentID)
        {
            String str = String.Format(@"SELECT * FROM dbo.PPCostCenterChilds
                                            WHERE FK_PPCostCenterParentID = {0} 
                                                  AND AAStatus='Alive'
                                        "
                                            , iPPCostCenterParentID);
            return dal.GetDataSet(str);
        }

        public DataSet GetAllDataParentByCostCenterChild(int iPPCostCenterChildID)
        {
            String str = String.Format(@"SELECT * FROM dbo.PPCostCenterChilds
                                            WHERE FK_PPCostCenterID = {0}
                                            AND AAStatus='Alive'
                                        "
                                            , iPPCostCenterChildID);
            return dal.GetDataSet(str);
        }
	}
