	public class PPEnvScoresController:BaseBusinessController
	{
		public PPEnvScoresController()
		{
			dal= new DALBaseProvider("PPEnvScores",typeof(PPEnvScoresInfo));
		}
        public double GetEfficiencyPctByScore(double dbScore)
        {
            string strQuery = string.Format(@"Select * from PPEnvScores where AAStatus='Alive' AND PPEnvScoreFromScore<={0} AND PPEnvScoreToScore>={0}", dbScore);
            DataSet ds = dal.GetDataSet(strQuery);
            if(ds!=null&&ds.Tables.Count>0&&ds.Tables[0].Rows.Count>0)
            {
                PPEnvScoresInfo EnvScore = (PPEnvScoresInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
                return EnvScore.PPEnvScoreEfficiencyPct;
            }
            return 0;
        }
	}
	#endregion
