	public class PPPkgOrdersController:BaseBusinessController
	{
		public PPPkgOrdersController()
		{
			dal= new DALBaseProvider("PPPkgOrders",typeof(PPPkgOrdersInfo));
		}
        public PPPkgOrdersInfo GetLastPkgOrderInMonth(DateTime dte)
        {
            string strDate = DALUtil.GenerateBeetween("PPPkgOrderDate", new DateTime(dte.Year, dte.Month, 01), new DateTime(dte.Year, dte.Month, 01).AddMonths(1).AddDays(-1));
            String strQuery = String.Format(@"SELECT Top 1 * FROM PPPkgOrders WHERE AAStatus = 'Alive' AND {0}
                                                Order by PPPkgOrderNo Desc", strDate);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0 && ds.Tables[0].Rows[0][0] != DBNull.Value)
                return (PPPkgOrdersInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            return null;
        }

        public DataSet GetPkgOrder(DateTime dtFrom, DateTime dtTo)
        {
            String strQuery = String.Format(@"SELECT * FROM dbo.PPPkgOrders WHERE AAStatus='Alive' 
                                            AND CONVERT(VARCHAR(10), [PPPkgOrderDate], 112)>= CONVERT(VARCHAR(10), '{0}', 112) 
                                            AND CONVERT(VARCHAR(10), [PPPkgOrderDate], 112)<= CONVERT(VARCHAR(10), '{1}', 112)", dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            return dal.GetDataSet(strQuery);
        }

	}
	#endregion
