	public class PPPkgStocksController:BaseBusinessController
	{
		public PPPkgStocksController()
		{
			dal= new DALBaseProvider("PPPkgStocks",typeof(PPPkgStocksInfo));
		}

        /// <summary>
        /// Get Object when Stock Use Update Inventory
        /// </summary>
        /// <returns></returns>
        public PPPkgStocksInfo GetObjectByStockUpdateInv()
        {
            string strQuery = string.Format(@"Select top 1 * from PPPkgStocks where AAStatus='Alive'
                                                        Order by PPPkgStockIsUpdateInv Desc");
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (PPPkgStocksInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            return null;
        }
	}
