        public DataSet GetAllDataSUMByYearANDPeriod(int iYear, int iPeriod, int iPPProductionCostCenterID, int iICProductID)
        {
            String str = String.Format(@"   SELECT  PPProductionCostCenterAllocates.FK_PPProductionCostCenterID ,
                                                    PPProductionCostCenterAllocates.FK_PPProductionCostFactorID ,
                                                    FK_ICProductID ,
                                                    PPProductionCostCenterAllocates.FK_PPCostCenterID ,
                                                    PPProductionCostCenterAllocates.FK_PPCostFactorID ,
                                                    FK_GLAccountID ,
                                                    PPProductionCostCenterAllocates.PPYear ,
                                                    PPProductionCostCenterAllocates.PPPeriod ,PPProductionCostFactorAllocates.PPProductionCostFactorAllocateOrder,
                                                    SUM(PPProductionCostCenterAllocateAmt) AS PPProductionCostCenterAllocateAmt
                                            FROM    dbo.PPProductionCostFactorAllocates
                                                    INNER JOIN dbo.PPProductionCostCenterAllocates ON ( PPProductionCostCenterAllocates.FK_PPCostCenterID = PPProductionCostFactorAllocates.FK_PPCostCenterID
                                                                                                        AND PPProductionCostCenterAllocates.FK_PPCostFactorID = PPProductionCostFactorAllocates.FK_PPCostFactorID
                                                                                                        AND PPProductionCostCenterAllocates.FK_PPProductionCostCenterID = PPProductionCostFactorAllocates.FK_PPProductionCostCenterID
                                                                                                        AND PPProductionCostCenterAllocates.FK_PPProductionCostFactorID = PPProductionCostFactorAllocates.FK_PPProductionCostFactorID
                                                                                                        AND PPProductionCostCenterAllocates.PPPeriod = PPProductionCostFactorAllocates.PPPeriod
                                                                                                        AND PPProductionCostCenterAllocates.PPYear = PPProductionCostFactorAllocates.PPYear
                                                                                                        AND PPProductionCostCenterAllocates.PPYear = {0}
                                                                                                        AND PPProductionCostCenterAllocates.PPPeriod = {1}
                                                                                                        AND PPProductionCostCenterAllocates.FK_PPProductionCostCenterID = {2}
                                                                                                        AND PPProductionCostCenterAllocates.FK_ICProductID = {3}
                                                                                                      )
                                            GROUP BY PPProductionCostCenterAllocates.FK_PPProductionCostCenterID ,
                                                    PPProductionCostCenterAllocates.FK_PPProductionCostFactorID ,
                                                    FK_ICProductID ,
                                                    PPProductionCostCenterAllocates.FK_PPCostCenterID ,
                                                    PPProductionCostCenterAllocates.FK_PPCostFactorID ,
                                                    FK_GLAccountID ,
                                                    PPProductionCostCenterAllocates.PPYear ,
                                                    PPProductionCostCenterAllocates.PPPeriod,
		                                            PPProductionCostFactorAllocates.PPProductionCostFactorAllocateOrder
                                            ORDER BY PPProductionCostFactorAllocates.PPProductionCostFactorAllocateOrder ASC"
                                            , iYear, iPeriod, iPPProductionCostCenterID, iICProductID);
            return dal.GetDataSet(str);
        }

