	public class PPProductionCostCenterChildsController:BaseBusinessController
	{
		public PPProductionCostCenterChildsController()
		{
			dal= new DALBaseProvider("PPProductionCostCenterChilds",typeof(PPProductionCostCenterChildsInfo));
		}

        public void DeleteAllDataByYearANDPeriod(int iYear, int iPeriod)
        {
            String str = String.Format(@"   DELETE dbo.PPProductionCostCenterChilds
                                            WHERE PPYear = {0}
                                            AND PPPeriod = {1}"
                                            , iYear, iPeriod);
            dal.GetDataSet(str);
        }

        public DataSet GetAllDataChildByYearANDPeriodANDCostCenterParent(int iYear, int iPeriod, int iPPProductionCostCenterParentID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPProductionCostCenterChilds
                                            WHERE PPYear = {0}
                                            AND PPPeriod = {1}
                                            AND FK_PPProductionCostCenterParentID = {2}"
                                            , iYear, iPeriod, iPPProductionCostCenterParentID);
            return dal.GetDataSet(str);
        }
        public DataSet GetAllDataParentByYearANDPeriodANDCostCenterChild(int iYear, int iPeriod, int iPPProductionCostCenterChildID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPProductionCostCenterChilds
                                            WHERE PPYear = {0}
                                            AND PPPeriod = {1}
                                            AND FK_PPProductionCostCenterID = {2}"
                                            , iYear, iPeriod, iPPProductionCostCenterChildID);
            return dal.GetDataSet(str);
        }
	}
