	public class PPProductionCostCenterStepsController:BaseBusinessController
	{
		public PPProductionCostCenterStepsController()
		{
			dal= new DALBaseProvider("PPProductionCostCenterSteps",typeof(PPProductionCostCenterStepsInfo));
		}

        public void DeleteAllDataByYearANDPeriod(int iYear, int iPeriod)
        {
            String str = String.Format(@"   DELETE dbo.PPProductionCostCenterSteps
                                            WHERE PPYear = {0}
                                            AND PPPeriod = {1}"
                                            , iYear, iPeriod);
            dal.GetDataSet(str);
        }
        public DataSet GetAllDataByYearANDPeriod(int iYear, int iPeriod)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPProductionCostCenterSteps
                                            WHERE PPYear = {0}
                                            AND PPPeriod = {1}
                                            ORDER BY PPProductionCostCenterStepOrder ASC"
                                            , iYear, iPeriod);
            return dal.GetDataSet(str);
        }

        public PPProductionCostCenterStepsInfo GetDataFirstOrderByYearANDPeriod(int iYear, int iPeriod)
        {

            DataSet ds = GetAllDataByYearANDPeriod(iYear, iPeriod);
            if(ds!= null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (PPProductionCostCenterStepsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        public PPProductionCostCenterStepsInfo GetDataNextCostCenterByYearANDPeriod(int iYear, int iPeriod, int iPPProductionCostCenterID)
        {
            String str = String.Format(@"   SELECT  Step1.*
                                            FROM    dbo.PPProductionCostCenterSteps Step1
                                                    INNER JOIN dbo.PPProductionCostCenterSteps Step2 ON ( Step2.PPProductionCostCenterStepOrder < Step1.PPProductionCostCenterStepOrder
                                                                                                          AND Step2.FK_PPProductionCostCenterID = {2}
                                                                                                          AND Step2.PPPeriod = Step1.PPPeriod
                                                                                                          AND Step2.PPYear = Step1.PPYear
                                                                                                        )
                                            WHERE   Step1.PPYear = {0}
                                                    AND Step1.PPPeriod = {1}
                                            ORDER BY Step1.PPProductionCostCenterStepOrder ASC"
                                            , iYear, iPeriod, iPPProductionCostCenterID);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (PPProductionCostCenterStepsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }

        public Boolean CheckDataInYearANDPeriod(int iYear, int iPeriod)
        {
            DataSet ds = GetAllDataByYearANDPeriod(iYear, iPeriod);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0) return true;
            return false;
        }
	}
