        public void DeleteAllDataByYearANDPeriod(int iYear, int iPeriod)
        {
            String str = String.Format(@"   DELETE dbo.PPProductionCostFactorItems
                                            WHERE FK_PPProductionCostFactorID IN (
                                                                            SELECT PPProductionCostFactorID FROM dbo.PPProductionCostFactors
                                                                            WHERE PPYear = {0} 
                                                                            AND PPPeriod = {1}
                                                                        )"
                                            , iYear, iPeriod);
            dal.GetDataSet(str);
        }

        public DataSet GetAllDataByYearANDPeriod(int iYear, int iPeriod, String strTypeCombo)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPProductionCostFactorItems
                                            WHERE FK_PPProductionCostFactorID IN (
                                                                            SELECT PPProductionCostFactorID FROM dbo.PPProductionCostFactors
                                                                            WHERE PPYear = {0} 
                                                                            AND PPPeriod = {1}
                                                                            AND PPProductionCostFactorTypeCombo = '{2}'
                                                                        )"
                                            , iYear, iPeriod, strTypeCombo);
            return dal.GetDataSet(str);
        }

        public PPProductionCostFactorItemsInfo GetDataByYearANDPeriod(int iYear, int iPeriod, int iGLAccountID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPProductionCostFactorItems
                                            WHERE FK_GLAccountID = {2}
                                            AND FK_PPProductionCostFactorID IN (
                                                                            SELECT PPProductionCostFactorID FROM dbo.PPProductionCostFactors
                                                                            WHERE PPYear = {0} 
                                                                            AND PPPeriod = {1}
                                                                        )"
                                            , iYear, iPeriod);
            DataSet ds = dal.GetDataSet(str);
            if(ds!= null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (PPProductionCostFactorItemsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }
	}
