	public class PPRoutingCustomerReqItemsController:BaseBusinessController
	{
		public PPRoutingCustomerReqItemsController()
		{
			dal= new DALBaseProvider("PPRoutingCustomerReqItems",typeof(PPRoutingCustomerReqItemsInfo));
		}

        #region Lấy tất cả thuộc tính sản phẩm theo khách hàng --Vinh

        ///<summary>
        ///Vinh 2014.05.26
        ///Lấy tất cả thuộc tính sản phẩm theo khách hàng. Lấy bộ thuộc tính cuối cùng nhất
        ///</summary>
        public DataSet GetAllDataLastByCustomerANDProduct(int iARCustomerID, int iICProductID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPRoutingCustomerReqItems
                                            WHERE AAStatus = 'Alive'
                                            AND FK_PPRoutingID IN (
						                                            SELECT TOP 1 PPRoutingID FROM dbo.PPRoutings
						                                            WHERE AAStatus = 'Alive'
						                                            AND FK_ARCustomerID = {0}
						                                            AND PPRoutingID IN (
												                                            SELECT FK_PPRoutingID FROM dbo.PPRoutingItems
												                                            WHERE AAStatus = 'Alive'
												                                            AND FK_ICProductID = {1}
											                                            )
                                                                    ORDER BY PPRoutingID DESC
						                                            )
                                        ", iARCustomerID, iICProductID);
            return dal.GetDataSet(str);
        }

        #endregion
    }
	#endregion
