	public class PPRoutingDrawingsController:BaseBusinessController
	{
		public PPRoutingDrawingsController()
		{
			dal= new DALBaseProvider("PPRoutingDrawings",typeof(PPRoutingDrawingsInfo));
		}

        public Boolean CheckPPRoutingDrawingsExist(int iPPRoutingID, String strFileName)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPRoutingDrawings
                                            WHERE AAStatus = 'Alive'
                                            AND FK_PPRoutingID = {0}
                                            AND PPRoutingDrawingNo LIKE N'{1}'
                                            ", iPPRoutingID, strFileName);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return true;
            }
            return false;
        }
	}
	#endregion
