	public class PPRoutingItemsController:BaseBusinessController
	{
		public PPRoutingItemsController()
		{
			dal= new DALBaseProvider("PPRoutingItems",typeof(PPRoutingItemsInfo));
		}
        public int GetPPRoutingIDByProduct(int iProductID)
        {
            int iPPRoutingID = 0;
            string strQuery = string.Format(@"Select top 1 FK_PPRoutingID from PPRoutingItems where AAStatus='Alive' AND FK_ICProductID={0} AND
                                               FK_PPRoutingID in (Select PPRoutingID from PPRoutings where AAStatus='Alive')", iProductID);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0 && ds.Tables[0].Rows[0][0] != DBNull.Value)
                int.TryParse(ds.Tables[0].Rows[0][0].ToString(), out iPPRoutingID);
            return iPPRoutingID;
        }

        #region Lấy tất cả chi tiết theo sản phẩm và khách hàng --Vinh

        /// <summary>
        /// Vinh --2014.05.27
        /// Lấy tất  cả chi tiết của Routing dựa vào khách hàng và sản phẩm
        /// </summary>
        public DataSet GetAllDataByCustomerANDProduct(int iARCustomerID, int iICProductID)
        {
            String str = String.Format(@"   SELECT  * FROM dbo.PPRoutingItems
                                            WHERE AAStatus = 'Alive'
                                            AND FK_ICProductID = {1}
                                            AND FK_PPRoutingID IN (
							                                            SELECT PPRoutingID FROM dbo.PPRoutings
							                                            WHERE AAStatus = 'Alive'
							                                            AND FK_ARCustomerID = {0}
						                                            )
                                            ", iARCustomerID, iICProductID);
            return dal.GetDataSet(str);
        }       

        #endregion
    }
