	public class PPRoutingOperationResourcesController:BaseBusinessController
	{
		public PPRoutingOperationResourcesController()
		{
			dal= new DALBaseProvider("PPRoutingOperationResources",typeof(PPRoutingOperationResourcesInfo));
		}
        public int GetFirstResourceByRoutingAndPhase(int iPPRoutingID, int iPPPhaseCfgID, string strResourceType)
        {
            string strQuery = string.Format(@"Select * from PPRoutingOperationResources where AAStatus='Alive'
                                                AND FK_PPRoutingID = {0}
                                                AND FK_PPRoutingOperationID in (select PPRoutingOperationID from PPRoutingOperations where AAStatus='Alive' 
                                                                                        AND FK_PPRoutingID = {0}
                                                                                        AND FK_PPPhaseCfgID={1})
                                                AND FK_PPResourceID in (Select PPResourceID from PPResources where AAStatus='Alive' AND PPResourceTypeCombo='{2}')"
                                                , iPPRoutingID, iPPPhaseCfgID, strResourceType);
            DataSet ds = dal.GetDataSet(strQuery);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                PPRoutingOperationResourcesInfo RoutingOperationResource = (PPRoutingOperationResourcesInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
                return RoutingOperationResource.FK_PPResourceID;
            }
            return 0;
        }
    }
	#endregion
