	public class PPRoutingTechnicalsController:BaseBusinessController
	{
		public PPRoutingTechnicalsController()
		{
			dal= new DALBaseProvider("PPRoutingTechnicals",typeof(PPRoutingTechnicalsInfo));
        }

        #region Lấy tất cả dữ liệu theo Routing và RoutingTechnical Parent --Vinh

        public DataSet GetAllDataByRouting(int iPPRoutingID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPRoutingTechnicals
                                            WHERE AAStatus = 'Alive'
                                            AND FK_PPRoutingID = {0}
                                            ", iPPRoutingID);
            return dal.GetDataSet(str);
        }

        #endregion

        #region Lấy tất cả dữ liệu theo Routing và RoutingTechnicalParent --Vinh

        public DataSet GetAllDataByRoutingANDTechnicalParent(int iPPRoutingID, int iPPRoutingTechnicalParentID)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PPRoutingTechnicals
                                            WHERE AAStatus = 'Alive'
                                            AND FK_PPRoutingID = {0}
                                            AND FK_PPRoutingTechnicalID = {1}
                                            ", iPPRoutingID, iPPRoutingTechnicalParentID);
            return dal.GetDataSet(str);
        }

        #endregion
    }
	#endregion
