        public DataSet GetSumQtyByARSOIID(int iARSOID)
        {
            string strQuery = string.Format(@"  SELECT PPWORItems.FK_ARSOID,
                                                       PPWORItems.FK_ARSOItemID,
                                                       SUM(PPWORItems.PPWORItemQty) AS PPWORItemQty
                                                FROM dbo.PPWORs
                                                    INNER JOIN dbo.PPWORItems
                                                        ON PPWORItems.FK_PPWORID = PPWORs.PPWORID
                                                           AND dbo.PPWORs.AAStatus = 'Alive'
                                                WHERE PPWORItems.AAStatus = 'Alive'
                                                      AND PPWORItems.FK_ARSOID = {0}
                                                GROUP BY PPWORItems.FK_ARSOID,
                                                         PPWORItems.FK_ARSOItemID;", iARSOID);
            return dal.GetDataSet(strQuery);
        }
