	public class PPProductionLossRatiosController:BaseBusinessController
	{
		public PPProductionLossRatiosController()
		{
			dal= new DALBaseProvider("PPProductionLossRatios",typeof(PPProductionLossRatiosInfo));
		}
        //Lấy dữ liệu không trùng nhau theo sản phẩm
        public DataSet GetDataGroupByProduct()
        {
            string str = (@"SELECT *
                            FROM dbo.PPProductionLossRatios
                            WHERE dbo.PPProductionLossRatios.PPProductionLossRatioID IN (SELECT MIN(PPProductionLossRatioID)
											                                             FROM dbo.PPProductionLossRatios
											                                             WHERE dbo.PPProductionLossRatios.AAStatus='Alive'
											                                             GROUP BY dbo.PPProductionLossRatios.FK_ICProductID)");
            return dal.GetDataSet(str);
        }
	}
