	public class PPProductionMOQRatiosController:BaseBusinessController
	{
		public PPProductionMOQRatiosController()
		{
			dal= new DALBaseProvider("PPProductionMOQRatios",typeof(PPProductionMOQRatiosInfo));
		}
        //Lấy dữ liệu không trùng nhau theo sản phẩm
        public DataSet GetDataGroupByProduct()
        {
            string str = (@"SELECT *
                            FROM dbo.PPProductionMOQRatios
                            WHERE dbo.PPProductionMOQRatios.PPProductionMOQRatioID IN (SELECT MIN(PPProductionMOQRatioID)
											                                             FROM dbo.PPProductionMOQRatios
											                                             WHERE dbo.PPProductionMOQRatios.AAStatus='Alive'
											                                             GROUP BY dbo.PPProductionMOQRatios.FK_ICProductID)");
            return dal.GetDataSet(str);
        }
	}
