	public class PPResourceUOMsController:BaseBusinessController
	{
		public PPResourceUOMsController()
		{
			dal= new DALBaseProvider("PPResourceUOMs",typeof(PPResourceUOMsInfo));
		}
        public PPResourceUOMsInfo GetResourceUOMActive()
        {
            string sQry = string.Format(@"  SELECT *
                                            FROM PPResourceUOMs
                                            WHERE AAStatus = 'Alive'
                                            AND PPResourceUOMDefault = 1");
            DataSet ds = GetDataSet(sQry);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
                return (PPResourceUOMsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            else
                return null;
        }
    }
