	public class PRSalaryAllowancesController:BaseBusinessController
	{
		public PRSalaryAllowancesController()
		{
			dal= new DALBaseProvider("PRSalaryAllowances",typeof(PRSalaryAllowancesInfo));
		}

        public void XoaTinhPhuCap(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"DELETE dbo.PRSalaryAllowances
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryAllowanceDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryAllowanceDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            dal.GetDataSet(strQry);
        }

        public DataSet LayDanhSachPhuCap(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"SELECT * FROM dbo.PRSalaryAllowances
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryAllowanceDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryAllowanceDate, 112) <= '{2}'",
                                         iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
           return GetDataSet(strQry);
        }

	}
