	public class PRSalaryBonusAllowancesController:BaseBusinessController
	{
		public PRSalaryBonusAllowancesController()
		{
			dal= new DALBaseProvider("PRSalaryBonusAllowances",typeof(PRSalaryBonusAllowancesInfo));
		}

        public void XoaTinhBienDongTienLuong(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"DELETE dbo.PRSalaryBonusAllowances
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryBonusAllowanceFromDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryBonusAllowanceToDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            dal.GetDataSet(strQry);
        }

        public DataSet LayDanhSachBienDongTienLuong(int iHREmployeeID, DateTime dtFrom, DateTime dtTo, String strHRPayrollTypeAttributeCombo, String strHRPayrollTypeTaxableCombo)
        {
            string strQry = string.Format(@"SELECT  *
                                            FROM    dbo.PRSalaryBonusAllowances
                                            WHERE   FK_HREmployeeID = {0}
                                                    AND CONVERT(VARCHAR, PRSalaryBonusAllowanceFromDate, 112) >= '{1}'
                                                    AND CONVERT(VARCHAR, PRSalaryBonusAllowanceToDate, 112) <= '{2}'
		                                            AND FK_HRPayrollTypeID IN (
		                                                                        SELECT HRPayrollTypeID FROM dbo.HRPayrollTypes
		                                                                        WHERE HRPayrollTypeAttributeCombo LIKE '{3}%'
		                                                                        AND HRPayrollTypeTaxableCombo LIKE '{4}%'
		                                                                       )",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"),strHRPayrollTypeAttributeCombo,strHRPayrollTypeTaxableCombo);
            return GetDataSet(strQry);
        }
	}
	#endregion
}