	public class PRSalaryOffWorksController:BaseBusinessController
	{
		public PRSalaryOffWorksController()
		{
			dal= new DALBaseProvider("PRSalaryOffWorks",typeof(PRSalaryOffWorksInfo));
		}

        public void XoaTinhNghiPhep(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"DELETE dbo.PRSalaryOffWorks
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryOffWorkDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryOffWorkDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            dal.GetDataSet(strQry);
        }

        public DataSet LayDanhSachNghiPhep(int iHREmployeeID, DateTime dtFrom, DateTime dtTo, String strOffWorkType)
        {
            string strQry = string.Format(@"SELECT  *
                                            FROM    dbo.PRSalaryOffWorks
                                            WHERE   FK_HREmployeeID = {0}
                                                    AND CONVERT(VARCHAR, PRSalaryOffWorkDate, 112) >= '{1}'
                                                    AND CONVERT(VARCHAR, PRSalaryOffWorkDate, 112) <= '{2}'
                                                    AND HREmployeeOffWorkTypeCombo = '{3}'",
                                         iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"), strOffWorkType);
            return GetDataSet(strQry);
        }
	}
	#endregion
