	public class PRSalaryOverTimesController:BaseBusinessController
	{
		public PRSalaryOverTimesController()
		{
			dal= new DALBaseProvider("PRSalaryOverTimes",typeof(PRSalaryOverTimesInfo));
		}

        public void XoaTinhTangCa(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"DELETE dbo.PRSalaryOverTimes
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryOverTimeDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryOverTimeDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            dal.GetDataSet(strQry);
        }

        public DataSet LayDanhSachTangCa(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"SELECT * FROM dbo.PRSalaryOverTimes
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryOverTimeDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryOverTimeDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            return GetDataSet(strQry);
        }
	}
