	public class PRSalaryShiftsController:BaseBusinessController
	{
		public PRSalaryShiftsController()
		{
			dal= new DALBaseProvider("PRSalaryShifts",typeof(PRSalaryShiftsInfo));
		}

        public void XoaTinhCongThuong(int iHREmployeeID, DateTime dtFrom,DateTime dtTo)
        {
            string strQry = string.Format(@"DELETE dbo.PRSalaryShifts
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryShiftDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryShiftDate, 112) <= '{2}'",
                                          iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            dal.GetDataSet(strQry);
        }
        public DataSet LayDanhSachCongThuong(int iHREmployeeID, DateTime dtFrom, DateTime dtTo)
        {
            string strQry = string.Format(@"Select * FROM dbo.PRSalaryShifts
                                            WHERE FK_HREmployeeID={0} 
                                            AND CONVERT(VARCHAR,PRSalaryShiftDate, 112) >= '{1}'
                                            AND CONVERT(VARCHAR,PRSalaryShiftDate, 112) <= '{2}'",
                                            iHREmployeeID, dtFrom.ToString("yyyyMMdd"), dtTo.ToString("yyyyMMdd"));
            return GetDataSet(strQry);
        }
	}
	#endregion
