	public class PRSlrsController:BaseBusinessController
	{
		public PRSlrsController()
		{
			dal= new DALBaseProvider("PRSlrs",typeof(PRSlrsInfo));
		}

        public PRSlrsInfo GetSlrByMonthANDYear(int iYear, int iMonth)
        {
            String str = String.Format(@"   SELECT * FROM dbo.PRSlrs
                                            WHERE AAStatus = 'Alive'
                                            AND PRSlrYear = {0}
                                            AND PRSlrPeriod = {1}", iYear, iMonth);
            DataSet ds = dal.GetDataSet(str);
            if (ds != null && ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                return (PRSlrsInfo)dal.GetObjectFromDataRow(ds.Tables[0].Rows[0]);
            }
            return null;
        }
	}
	#endregion
}