	public class ViewADUsersController:BaseBusinessController
	{
		public ViewADUsersController()
		{
			dal= new DALBaseProvider("ViewADUsers",typeof(ViewADUsersInfo));
		}

        public DataSet GetAllUser()
        {
            String strQuery = String.Format(@"SELECT ADUserName AS ViewADUserName,
                                ADUserGroupName AS UserGroup,
                                CONVERT(BIT,0) AS AASelected
                                 FROM dbo.ADUsers,dbo.ADUserGroups
                                 WHERE dbo.ADUserGroups.AAStatus='Alive'
                                 AND dbo.ADUsers.AAStatus='Alive'
                                 AND ADUsers.ADUserGroupID=ADUserGroups.ADUserGroupID
                                 ");
            return GetDataSet(strQuery);
        }
    }
	#endregion
