	public class ViewPOStatisticsController:BaseBusinessController
	{
		public ViewPOStatisticsController()
		{
			dal= new DALBaseProvider("ViewPOStatistics",typeof(ViewPOStatisticsInfo));
		}
        public DataSet GetViewPOReceiptItemStatisticbySupplier(int ReceiptItemID)
        {
            string strQuery = string.Format(@"SELECT dbo.APPOs.APPOID,
		                                                dbo.APPOs.FK_APSupplierID,
		                                                dbo.APPOs.APPODate,
		                                                dbo.APPOs.APPONo,
		                                                dbo.APPOItems.FK_ICProductID,
		                                                dbo.APPOItems.APPOItemQty,
		                                                dbo.ICReceiptItems.ICReceiptItemQty
                                            FROM dbo.ICReceiptItems INNER JOIN ICReceiptItemPurchaseOrderItems 
                                                                    ON ICReceiptItems.ICReceiptItemID = ICReceiptItemPurchaseOrderItems.FK_ICReceiptItemID
						                                            INNER JOIN APPOItems
                                                                    ON ICReceiptItemPurchaseOrderItems.FK_APPOItemID = APPOItems.APPOItemID
						                                            INNER JOIN APPOs 
                                                                    ON APPOItems.FK_APPOID = APPOs.APPOID
                                            WHERE ICReceiptItems.ICReceiptItemID = {0}", ReceiptItemID);
            DataSet ds = dal.GetDataSet(strQuery);
            return ds;
        }
	}
	#endregion
